import { Component, OnInit } from '@angular/core';
import { TokenStorageService } from '../_services/token-storage.service';
import { UserService } from '../_services/user.service';

@Component({
  selector: 'app-all-my-books',
  templateUrl: './all-my-books.component.html',
  styleUrls: ['./all-my-books.component.css']
})
export class AllMyBooksComponent implements OnInit {
  content?: string[];

  books :any = [];
  //jsonVar  = {};

  constructor(private userService: UserService,private tokenStorageService: TokenStorageService) { }

  isLoggedIn = false;
  authorId : number = 0;

  async ngOnInit(): Promise<void> {
    this.isLoggedIn = !!this.tokenStorageService.getToken();
    if (this.isLoggedIn) {
      const user = this.tokenStorageService.getUser();
      this.authorId = user.id;
    }
    await this.userService.getAllBooks(this.authorId).subscribe(
      data => {
      this.books = data;

      },
      err => {
        this.content = JSON.parse(err.error).message;
      }
    );
    alert(this.books.length);
    this.books.forEach((element : any) => {
      console.log(element);
      // console.log(element.logo);
      // console.log(element.title);
      // console.log(element.category );
      // console.log(element.price);
      // console.log(element.authorId);
    });

  }

}
