import { Component, OnInit } from '@angular/core';
import { TokenStorageService } from '../_services/token-storage.service';
import { UserService } from '../_services/user.service';

@Component({
  selector: 'app-add-book',
  templateUrl: './add-book.component.html',
  styleUrls: ['./add-book.component.css']
})
export class AddBookComponent implements OnInit {

  constructor(private userService: UserService, private tokenStorageService: TokenStorageService) { }

  isLoggedIn = false;
  readerEmail : string = "";
  readerId : number = 0;

  selectedFiles?: FileList;
  currentFile?: File;

  create = {
    title : "",
    category : "",
    image : File,
    price:"",
    publisher : "",
    active: "",
    content : ""
  }

  ngOnInit(): void {
    this.isLoggedIn = !!this.tokenStorageService.getToken();
    if (this.isLoggedIn) {
      const user = this.tokenStorageService.getUser();
      this.readerEmail = user.email;
      this.readerId = user.id;
    }
  }


  createBook(){
    console.log(this.create.title);
    console.log(this.create.category);
    console.log(this.create.image);
    console.log(this.create.price);
    console.log(this.create.publisher);
    console.log(this.create.active);
    console.log(this.create.content);
  }

}
